/*Table structure for table `t_order_detail` */

DROP TABLE IF EXISTS `t_order_detail`;

CREATE TABLE `t_order_detail` (
  `order_DetailId` int(11) NOT NULL AUTO_INCREMENT,
  `order_Id` varchar(50) DEFAULT NULL COMMENT '订单主键',
  `order_no` varchar(50) DEFAULT NULL COMMENT '订单编号',
  `product_id` int(11) DEFAULT NULL COMMENT '商品编号',
  `vip_id` int(11) DEFAULT NULL COMMENT '会员编号',
  `order_amount` int(11) DEFAULT NULL COMMENT '订购数量',
  `post_status` varchar(50) DEFAULT '未发货' COMMENT '默认为未发货，取“未发货”或“已发货”',
  `recev_status` varchar(50) DEFAULT '未收货' COMMENT '默认为未收货，取“未收货”或“已收货”',
  `sale_totalprice` decimal(10,2) DEFAULT NULL COMMENT '卖出总价',
  `del_flag` int(2) DEFAULT '0',
  `product_price` decimal(10,2) DEFAULT NULL COMMENT '商品卖出的实际价格',
  PRIMARY KEY (`order_DetailId`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

/*Data for the table `t_order_detail` */

/*Table structure for table `t_order_info` */

DROP TABLE IF EXISTS `t_order_info`;

CREATE TABLE `t_order_info` (
  `order_id` varchar(50) NOT NULL,
  `order_no` varchar(50) DEFAULT NULL COMMENT '订单编号 yyyymmddhhmmss',
  `vip_id` int(11) DEFAULT NULL COMMENT '会员编号',
  `order_date` datetime DEFAULT NULL COMMENT '下单日期',
  `order_amount` int(11) DEFAULT NULL COMMENT '总订购数量',
  `order_message` varchar(100) DEFAULT NULL COMMENT '留言',
  `pay_method` varchar(100) DEFAULT NULL COMMENT '支付方式',
  `recever_name` varchar(50) DEFAULT NULL COMMENT '收货人姓名',
  `recever_addr` varchar(2000) DEFAULT NULL COMMENT '收货地址',
  `recever_tel` varchar(50) DEFAULT NULL COMMENT '收货人电话号码',
  `memo` varchar(200) DEFAULT NULL COMMENT '备注',
  `total_price` decimal(10,2) DEFAULT NULL COMMENT '总卖出价',
  `order_status` int(2) DEFAULT '0' COMMENT '0为已确认，待支付 1为已支付，待发货 2为已发货，待收货 3为已收货完成',
  `del_flag` int(2) DEFAULT '0',
  PRIMARY KEY (`order_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

